def soustrait_carre(n) :    """Fonction retournant le couple de nombre p² et n-p²"""    p = int(n**0.5)    diff = n-p**2    return [diff,p]def decomposition_carre(n) :    """Fonction retournant la liste des carrés de la décomposition"""    decomp = soustrait_carre(n)    diff = decomp[0]    p = decomp[1]    if diff == 0 :        return [p]    else :        decomp = decomposition_carre(diff)        decomp.append(p)        return decompdef affichage(n,L) :    """Procédure affichant la décomposition du nombre n, en une somme de carrés"""    print(str(n), " = ", end = "")    fin = " + "    for i in range(len(L)-1,-1,-1) :        if i < 1 :            fin = ""        print(str(L[i]), "²", sep="", end = fin)        n = int(input("Entrer un nombre entier : "))couple = soustrait_carre(n)print("Le couple de nombre (n,diff) tel que p² \u2264 n < (p+1)² et diff = n - p² est", couple)decomp = decomposition_carre(n)print("Les éléments de la décompositon en carré sont : ", decomp)affichage(n, decomp)